#[cfg(debug_assertions)]
use crate::*;

impl Solution {
    pub fn number_of_alternating_groups(colors: Vec<i32>, k: i32) -> i32 {
        let n = colors.len();
        let mut dp = vec![1; n];
        for w in colors.windows(2).rev() {
            if w[0] != w[1] {
                dp[n - 1] += 1;
            } else {
                break;
            }
        }
        for i in 0..n - 1 {
            let j = (i + n - 1) % n;
            dp[i] = if colors[i] != colors[j] { dp[j] + 1 } else { 1 };
        }
        let mut ans = 0;
        for i in 0..n {
            if dp[i] >= k as usize {
                ans += 1;
            }
        }
        ans
    }
}
