public class Solution {
    public int numberOfAlternatingGroups(int[] colors, int k) {
        int length = colors.length;
        if (k > length) {
            return 0;
        }

        int sameCount = 0;
        for (int j = 0; j < k - 1; j++) {
            if (colors[j] == colors[j + 1]) {
                sameCount++;
            }
        }

        int groupCount = 0;
        if (sameCount == 0) {
            groupCount++;
        }

        for (int i = 1; i < length; i++) {
            if (colors[i - 1] == colors[(i - 1 + 1) % length]) {
                sameCount--;
            }
            if (colors[(i + k - 1) % length] == colors[(i + k - 2) % length]) {
                sameCount++;
            }

            if (sameCount == 0) {
                groupCount++;
            }
        }

        return groupCount;
    }
}
