class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n = colors.size();
        int cnt = 0;
        for (int i = 0; i < k - 1; ++i) {
            if (colors[i] == colors[(i + 1) % n]) ++cnt;
        }
        int ans = (cnt == 0);
        for (int i = 1; i < n; ++i) {
            if (colors[i - 1] == colors[i]) --cnt;
            if (colors[(i + k - 2) % n] == colors[(i + k - 1) % n]) ++cnt;
            ans += (cnt == 0);
        }
        return ans;
    }
};
