class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& v, int k) {
        int n = v.size();
        int ans(0);
        int j(0),i(0);
        while(i < n)
        {
            bool hua = 1;
            while((j-i) < k-1)
            {
                int index = j+1;
                if(v[index%n] == v[j%n])
                {
                    hua = 0;
                    j = index;
                    break;
                }
                j = index;
            }
            if(hua)
            {
                ++ans;
                ++i;
            }
            else
            {
                if(j >= n) break;
                i = j;
            }
        }
        return ans;
    }
};
