class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int qwq = 0, owo = 1, n = colors.size();

        for(int i = 0; i < k - 1; i++){
            colors.push_back(colors[i]);
        }

        for(int i = 1; i < colors.size(); i++){
            if(colors[i] != colors[i - 1]) owo++;
            else{
                if(owo >= k){
                    qwq += owo - k + 1;
                }
                owo = 1;
            }
        }

        if(owo >= k){
            qwq += owo - k + 1;
            owo = 0;
        }
        return qwq;
    }
};
