class Solution:
    def numberOfAlternatingGroups(self, colors: List[int], k: int) -> int:
        pre = colors[0]
        cnt = 0
        res = 0
        n = len(colors)
        for i in range(n + k - 1):
            color = colors[i % n]
            if color == pre:
                cnt = 1
                continue
            pre = color
            cnt += 1
            if cnt >= k:
                res += 1
        return res
