class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {


        int n = colors.size();
        int last = colors[n-1];

        vector<int> nw;

        for(int i=0;i<n;i++){
            nw.push_back(colors[i]);
        }

        for(int i=0;i<n;i++){
            nw.push_back(colors[i]);
        }

        int i = 0,j=0;
        int cnt = 0;

        while(i<n){
            while(j-i+1<k && j<2*n){
                if(i>=n) break;

                if(j<2*n && nw[j] == nw[j+1]){
                    i = j+1;
                    j = j+1;
                }
                else j++;
            }

            if(i<n) cnt++;

            if(j<2*n && nw[j] == nw[j+1]){
                i = j+1;
                j = j+1;
            }
            else{
                i++;
                j++;
            }
        }

        return cnt;


    }
};
