#define ll long long
#define all(v) v.begin(),v.end()
#define pb push_back
class Solution {
public:
    void fun(vector<int> &cp,vector<int> &v)
    {
        int n = v.size();
        for(int i=0;i<n;i++)
         cp.pb(v[i]);
    }
    int numberOfAlternatingGroups(vector<int>& v, int k) {

    int n = v.size();
     vector<int> cp=v;
      fun(cp,v);
      fun(cp,v);

int low=0,high=1,ans=0;

while(low<n)
{
    if(high-low < k)
    {
        if(cp[high]!=cp[high+1])
         high++;
        else
        {
            low=high;
            high++;
        } 
    }
    else
   {
    low++;
    ans++;  
   }
}
return ans;   
    }
};
