class Solution:
    def numberOfAlternatingGroups(self, colors: List[int], k: int) -> int:
        n=len(colors)
        nums=colors+colors[:k-1]
        r=0
        ls=[]
        m=len(nums)

        for i in range(1,m-1):
            if nums[i]!=nums[i-1] and nums[i]!=nums[i+1]:
                ls.append(1)
            else:
                ls.append(0)
        print(ls)
        m=len(ls)
        prels=[0]*(m+1)
        for i in range(m):
            prels[i]=prels[i-1]+ls[i]
            if i+1>=k-2 and prels[i]-prels[i-(k-2)]==k-2:
                r+=1
        return r
