class Solution:
    def numberOfAlternatingGroups(self, colors: List[int], k: int) -> int:
        n = len(colors)
        colors = colors + colors
        A = [0]
        res = 0

        for i in range(1, n * 2):
            if colors[i] == colors[i - 1]:
                A.append(1)
            else:
                A.append(0)
        P = list(accumulate(A))
        res = 0
        for i in range(n):
            if P[i + k - 1] - P[i] == 0:
                res += 1
        return res
