class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& a, int k) {
        for(int i = 1; i <= k; i++) a.push_back(a[i - 1]);
        int n = a.size();
        vector<int> dp(n);
        for(int i = 1; i < n; i++) dp[i] = dp[i - 1] + abs(a[i] - a[i - 1]);
        int ans = 0;
        for(int i = k; i < n; i++)
        {
            if(dp[i] - dp[i - k + 1] == (k - 1)) ans++;
        }
        return ans;
    }
};
