from typing import List

class Solution:
    def numberOfAlternatingGroups(self, colors: List[int], k: int) -> int:
        num_colors = len(colors)
        count = 0

        if k == 1:
            return num_colors

        alternating_length = 1
        i = 1
        while i < num_colors + k - 1:
            current = i
            previous = i - 1

            if current >= num_colors:
                current -= num_colors
            if previous >= num_colors:
                previous -= num_colors

            if colors[current] != colors[previous]:
                alternating_length += 1
            else:
                alternating_length = 1

            if alternating_length >= k:
                count += 1

            i += 1

        return count
