class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int count=0,n=colors.size();
        for(int i=0,j=1;j<2*n&&i<n;j++){
            if(colors[j%n]==colors[(j-1)%n] ){
                i=j;
            }
            if(j-i+1==k){
                count++;
                i++;
            }

        }
        return count;
    }
};
