package main

func numberOfAlternatingGroups(colors []int, k int) int {
	N := len(colors)
	count := 0

	if k == 1 {
		// Every single tile is an alternating group by itself
		return N
	}

	alternatingLength := 1
	i := 1
	for i < N+k-1 {
		current := i
		previous := i - 1

		// Handle circular indexing
		if current >= N {
			current -= N
		}
		if previous >= N {
			previous -= N
		}

		if colors[current] != colors[previous] {
			alternatingLength++
		} else {
			alternatingLength = 1
		}

		// Check if we have a valid alternating group of length k
		if alternatingLength >= k {
			count++
		}

		i++
	}

	return count
}
