class Solution:
    def numberOfAlternatingGroups(self, colors: List[int], k: int) -> int:
        left, right = 0, 1
        ans = 0
        colors = colors + colors[:k-1]
        n = len(colors)
        while right < n:
            while right < n and colors[right] != colors[right - 1]:
                right += 1
            if right - left >= k:
                ans += (right - left - k + 1)
            left = right
            right += 1
        return ans
