class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int ans = 0;
        int count = 1;
        for (size_t cur = colors.size() - k + 1; cur < colors.size(); ++cur) {
            if (colors[cur] != colors[cur - 1])
                ++count;
            else
                count = 1;
        }
        for (size_t cur = 0; cur < colors.size(); ++cur) {
            size_t prev = cur > 0 ? cur - 1 : colors.size() - 1;
            if (colors[cur] != colors[prev])
                ++count;
            else
                count = 1;
            if (count >= k)
                ++ans;
        }
        return ans;
    }
};
