class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int iter = 1;
        int n = colors.size();
        int len = 1;
        int cnt =0;
        while(iter!=0){
            if(colors[iter]==colors[(iter-1+n)%n]){
                len = 1;
            }else len++;

            if(len==k){
                len--;
                cnt++;
            }
            iter = (iter+1)%n;
        }
        while(iter<(k-1)){
            if(colors[iter]==colors[(iter-1+n)%n]){
                len = 1;
            }else len++;

            if(len==k){
                len--;
                cnt++;
            }
            iter = (iter+1)%n;
        }
        return cnt;
    }
};
