class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        vector<int> extendedColors;

        for(auto color : colors){
            extendedColors.push_back(color);
        }
        for(int i = 0; i < k - 1; i++){
            extendedColors.push_back(colors[i]);
        }

        int totalColors = extendedColors.size();
        int current = 1;  
        int left = 0; 
        int right = 1;  
        int Groups = 0;  


        while(right < totalColors){

            if(extendedColors[right] == !extendedColors[right - 1]){
                current++;
            }
            else{
                left = right; 
                current = 1;  
            }


            if(current == k) {
                left++;
                current--;
                Groups++;
            }
            right++;
        }

        return Groups;
    }
};
