class Solution:
    def numberOfAlternatingGroups(self, colors: List[int], k: int) -> int:
        arr = colors + colors
        n = len(arr)
        isValid = defaultdict(int)
        for i in range(1, n - 1):
            if arr[i - 1] != arr[i] != arr[i + 1]:
                isValid[i] = 1
            else:
                isValid[i] = 0
        pref = sum(isValid[x] for x in range(1, k - 1))
        ans = 1 if pref == k - 2 else 0
        for l in range(1, n//2):
            pref -= isValid[l]
            pref += isValid[l + k - 2]
            if pref == k - 2:
                ans += 1
        return ans
