class Solution {
public:
    int a[200005],sum[200005],n;
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        n=colors.size();
        for(int i=1;i<=n;i++)a[i]=colors[i-1];
        for(int i=1;i<=n;i++)a[i+n]=a[i];
        n=2*n;
        for(int i=2;i<=n;i++){
            if(a[i]!=a[i-1])sum[i]=sum[i-1]+1;
            else sum[i]=sum[i-1];
        }
        int res=0;
        for(int i=1;i<=n/2;i++){
            int l=i,r=i+k-1;
            if(sum[r]-sum[l]==k-1)res++;

        }
        return res;
    }
};
