class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        vector<int>v=colors;
        int n=v.size();
        for(auto x:colors) v.push_back(x);
        for(int i=0;i<v.size();i+=2){
            v[i]^=1;
        }
        int sm=0;
        for(int i=0;i<k;i++) sm+=v[i];
        int ans=0;
        if(sm==0||sm==k) ans++;
        for(int i=k;i<n+k-1;i++){
            sm+=v[i];
            sm-=v[i-k];
            if(sm==0||sm==k) ans++;
        }
        return ans;
    }
};
