class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& a, int k) {
        int n=a.size();
        int j=1;
        for(int i=0;i<n;i++) a.push_back(a[i]);
        k--;
        while(j<(n+n)) {
            if(a[j]==a[j-1]) {
                break;
            }
            j++;
        }
        int ans=0;
        for(int i=k;i<(n+k);i++) {
            if(j<=(i-k)) {
                j=(i-k)+1;
                while(j<(n+n)) {
                    if(a[j]==a[j-1]) {
                         break;
                     }
                     j++;
                }
            }
            ans+=(j>i);
        }
        return ans;
    }
};
