class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n = colors.size(), res = 0;
        for(int i = 0; i < k - 1; i++) colors.push_back(colors[i]);
        for(int i = 0, j = 0; j < n + k - 1; j++){
            if(i == j) continue;
            if(colors[j] == colors[j - 1]) i = j;
            else{
                if(j - i + 1 >= k) res++;
            } 
        }
        return res;
    }
};
