class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n = colors.size();
        colors.insert(colors.end(), colors.begin(), colors.end());
        int ans = 0;
        for (int i = 0; i < n; ) {
            int j = i;
            while (j + 1 < 2 * n && colors[j + 1] != colors[j]) {
                j++;
            }
            while (j - k + 1 >= n) {
                j--;
            }
            int len = j - i + 1;
            ans += max(0, len - k + 1);
            i = j + 1;
        }
        return ans;
    }
};
