class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
int N = colors.size();
    int count = 0;

    if (k == 1) {
        return N;
    }

    int currentAlternatingLength = 1;
    int index = 1;
    while (index < N + k - 1) {
        int currentIndex = index;
        int previousIndex = index - 1;

        if (currentIndex >= N) {
            currentIndex -= N;
        }
        if (previousIndex >= N) {
            previousIndex -= N;
        }

        if (colors[currentIndex] != colors[previousIndex]) {
            ++currentAlternatingLength;
        } else {
            currentAlternatingLength = 1;
        }

        if (currentAlternatingLength >= k) {
            ++count;
        }

        ++index;
    }

    return count;
    }
};
