class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) 
    {
        int n = colors.size();
        for (int i=0; i<n; i++)
            colors.push_back(colors[i]);

        int j = 0;
        int ret = 0;
        for (int i=0; i<n; )
        {
            j = i+1;
            while (j<=n-1+k-1 && colors[j]!=colors[j-1])
                j++;

            // cout<<i<<" "<<j<<" "<<max(0, j-k-i+1)<<endl;

            if (j-k>=i)
                ret += max(0, j-k-i+1);
            i = j;
        }
        return ret;

    }
};
