class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& c, int k) {
        int o=c.size();
        for(int i=0;i<k-1;i++)
        c.push_back(c[i]);
        int n=c.size();
        vector<int> dp(n);
        for(int i=0;i<n;i++)
        {
            int p=(i-1+n)%n;
            int x=(i+1)%n;
            if(i)
            dp[i]=dp[i-1];
            if(c[p]!=c[i]&&c[i]!=c[x])
            dp[i]++;
        }
        int ans=0;
        for(int i=0;i<o;i++)
        {
              if(dp[i+k-2]-dp[i]==k-2)
              ++ans;
        }
        return ans;

    }
};
