class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& a, int k) {
        int n=a.size();
        a.resize(n+n);
        for(int i=n;i<2*n;i++) a[i]=a[i-n];

        int cnt=0;
        for(int i=1;i<k;i++){
            if(a[i]==a[i-1]) cnt++;
        }
        int res=0;
        if(cnt==0) res+=1;
        for(int i=1,j=k;i<n;i++,j++){
            if(a[i]==a[i-1]) cnt--;
            if(a[j]==a[j-1]) cnt++;
            res+=(cnt==0);
        }
        return res;
    }
};
