        int n = v.size();
        int j  = 0 ;
        int  x = 0 ;
        int y = 0 ;
        int cnt  = 1 ;
        int ans  = 0 ;
        for (int  i = 1; i <n  ; i++){
        if (v[i]==v[i-1])
        {
            cnt  = 1 ;
        }
        else {
        cnt ++;
        if (cnt >=k)
        {
          ans ++;
        }
        }
        }
        for (int  i = 0 ; i<k-1 ; i++){
    if (i==0){
        if (v[i]==v[n-1])
        break ;
        else cnt ++;
        if (cnt >=k)
        ans ++;
    }
    else {
        if (v[i]!=v[i-1])
        cnt ++;
        else break ;
        if (cnt >=k)
        ans ++;
    }
        }
