class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& a, int m) {
        int n = a.size();
        a.resize(n * 2);
        for (int i = n; i < n * 2; ++i) a[i] = a[i - n];
        for (int i = 0; i < n * 2; ++i) if (i % 2) a[i] ^= 1;
        vector<int> S(n * 2 + 1);
        for (int i = 0; i < n * 2; ++i) S[i + 1] = S[i] + a[i];
        int ret = 0;
        for (int i = 0; i < n; ++i) {
            if (S[i + m] - S[i] == m * a[i]) ++ret;
        }
        return ret;
    }
};
