class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n = colors.size();

        vector<int> dp(2*n, 1);
        for(int i =0; i<n;i++)
            colors.push_back(colors[i]);

        dp[0] = 1;
        for(int i = 1; i<2*n; i++){
            if(colors[i]!=colors[i-1]){
                dp[i] = dp[i-1] + 1;
            }
            else{
                dp[i] = 1;
            }
        }

        int ans = 0;
        for(int i =0;i<2*n;i++){
            if(dp[i]>=k){
                if(i>=n){
                    if(i - k+1<=n-1)
                        ans++;
                }
                else
                    ans++;
            }
        }

        return ans;
    }
};
