class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& c, int k) {
        int n=c.size();
        int ans=0;
        for(int i=0; i<k-1; i++)
            c.push_back(c[i]);
        int l=0, r=1;

        while(r<c.size())
        {
            if(c[r]==c[r-1])
            {
                l=r;
                r=r+1;
            }
            else
            {
                if(r-l>=k)
                    l++;
                if(r-l+1==k)
                    ans++;

                r++;
            }
        }
        return ans;
    }
};
