class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        auto v2 = colors;
        for(auto i : colors) v2.push_back(i);
        int n = colors.size();
        vector<int> v(n*2, 0);
        v[0] = 1;
        for (int i = 1; i < v2.size(); i++) {
            if(v2[i] != v2[i-1]) v[i] = v[i-1]+1;
            else v[i] = 1;
        }
        // for (auto i : v) cout << i << " "; cout << endl;
        int ans = 0;
        for (int i = n; i < v.size(); i++) if(v[i]>=k) ans++;
        return ans;
    }
};
