class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& nums, int k) {

        int l = 0, r = k-1;

        int count = 0;
        for(int i=1; i<=r-1; i++)
        if(nums[i]!=nums[i-1] and nums[i]!=nums[i+1]) count++;

        int ans = (count==k-2);
        int n = nums.size();
        for(l = 1, r = k; l<n; l++, r++){
            r %= n;
            if(nums[l]!=nums[l-1] and nums[l]!=nums[(l+1)%n]) count--;
            if(nums[(r-1+n)%n]!=nums[(r-2+n)%n] and nums[(r-1+n)%n]!=nums[r]) count++;
            if(count==k-2) ans++;
        }
        return ans;
    }
};
