class Solution:
    def numberOfAlternatingGroups(self, colors: List[int], k: int) -> int:
        c = 1
        ans = 0
        p = colors[0]
        seen = set()
        for i, n in enumerate(colors):
            if n != p:
                c += 1
                if c >= k and i not in seen:
                    ans += 1
                    seen.add(i)
            else:
                c = 1
            p = n
        for i, n in enumerate(colors):
            if n != p:
                c += 1
                if c >= k and i not in seen:
                    ans += 1
            else:
                break
            p = n
        return ans
