class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n = colors.size();
        int curr = 1;
        int res = 0;
        int color = colors[0];
        for(int i=1;i<n;i++){
            if(colors[i] != color){
                color = colors[i];
                curr++;
            }
            else{
                if(curr >= k){
                    res+= (curr-k+1);
                }
                color = colors[i];
                curr = 1;
            }
        }

        int i = 0;
        while(i < k -1 ){
            if(colors[i] == color){
                if(curr >= k) return res+(curr-k+1);
                else return res;
            }
            else{
                color = colors[i];
                curr++;
                if(curr == n+1) return n;
            }
            i++;
        }
        if(curr >= k) res+= (curr-k+1);
        return res;

    }
};
