class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& c, int k) {
        int n = c.size();
    int cnt = 1,ans=0;
        for(int i=1;i<n+k-1;i++){
            if(c[i%n]!=c[(i-1)%n]){
                cnt++;
            }else{
                cnt=1;
            }
            if(cnt>=k)ans++;
        }
        return ans;
    }
};
