class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n = colors.size();
        for(int i=0; i<n; i++) colors.push_back(colors[i]);

        int cnt1 = 0, cnt2 = 0;

        int left =0;
        for(int i=1;i<2*n;i++){
            if(colors[i]!=colors[i-1]){
                if(i-left+1 >= k){
                    cnt1++;
                    if(i<n) cnt2++;
                }
            }else{
                left = i;
            }
        }
        // cout << cnt1 << cnt2 << endl;
        return cnt1 - cnt2;
    }
};
