class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n = colors.size();
		vector<int> a(n*3), f(n*3);
        a[0]=-1;
		rep(i,1,n) a[i]=a[i+n]=colors[i-1];
		f[0]=0;
		rep(i,1,n*2) {
			if (a[i]!=a[i-1]) f[i]=f[i-1]+1;
			else f[i]=1;
		}
		ll ans=0;
        rep(i,1,n*2) cout<<f[i]<<" ";cout<<endl;
		rep(i,n+1,n*2) {
			ans+=f[i]>=k;
		}
		return ans;
    }
};
