class Solution:
    def numberOfAlternatingGroups(self, arr: List[int], k: int) -> int:
        n = len(arr)
        groups1, groups2 = [], []
        count = 1

        for i in range(n-1):
            if arr[i] != arr[i+1]:
                count += 1
            else:
                if count >= k:
                    groups1.append(count)
                count = 1

        if count >= k:
            groups1.append(count)

        temp = arr[-k+1:] + arr[:k-1]
        count = 1

        for i in range(len(temp)-1):
            if temp[i] != temp[i+1]:
                count += 1
            else:
                if count >= k:
                    groups2.append(count)
                count = 1

        if count >= k:
            groups2.append(count)

        groups1.extend(groups2)

        result = 0
        for group in groups1:
            result += group - k + 1

        return result
