using System;
using System.Collections.Generic;

public class Solution {
    public int NumberOfAlternatingGroups(int[] colors, int k) {
        List<int> colorsList = new List<int>(colors);
        List<int> extendedColors = new List<int>(colorsList);
        extendedColors.AddRange(colorsList);

        int result = CountAlternatingGroups(extendedColors, colors.Length, k);

        return result;
    }

    private int CountAlternatingGroups(List<int> colors, int n, int k) {
        int start = 1;
        int end = 0;
        int result = 0;

        while (end < n && start - k < n) {
            if (colors[start] != colors[start - 1]) {
                start++;
            } else {
                end = start;
                start++;
            }

            int count = 0;
            if (start - end >= k) {
                count++;
                end++;
            }
            result += count;
        }

        return result;
    }
}
