class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& arr, int k) {
         int ans = 0;
        set<int> settt;
        int n = arr.size();
        for (int i = 0; i < n; i++) {
            if (arr[i] == arr[(i - 1 + n) % n]) settt.insert(i); 
        }
        for (int q = 0; q <= n - k; q++) {
            auto it = settt.lower_bound(q + 1);
            if (it != settt.end() && *it < q + k) continue;
            ans++;
        }
        for (int i = n - k + 1; i < n; i++) {
            int next = settt.empty() ? -1 : *settt.rbegin();
            if (next != -1 && next > i) {
                continue;
            }

            int prev = settt.empty() ? -1 : *settt.begin();
            if (prev != -1 && prev < k - (n - i)) {
                continue;
            }

            ans++;
        }

        return ans;
    }
};
