class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& c, int k) {

        int n = c.size();

        vector<int> pre = vector<int>(n, 1);
        for (int i = 1; i < n; i++) {

            if (c[i] != c[i - 1]) {
                pre[i] = pre[i - 1] + 1;
            }
        }



        if (c[0] != c[n - 1]) {

            pre[0] += pre[n - 1];
            int i = 1;
            while (i<n && pre[i] != 1) {
                pre[i] += pre[n - 1];
                i++;
            }
        }

        int ans = 0;

        for (auto a : pre) {
            if (a >= k) {
                ans++;
            }

        }
        return ans;
    }
};
