class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& nums, int k) 
    {
        int n= nums.size();
        // nums.append(nums);
        nums.insert( nums.end(), nums.begin(), nums.end() );
        int ans = 0;
        int j = 0;
        int cnt=1;
        for(int i = 1 ;i<n+k-1;i++)
        {
            if(nums[i]!=nums[i-1])
            {
                cnt++;
            }  
            else
            {
                cnt=1;
            }
            if(cnt==k)
            {
                cnt--;
                ans++;
            }
        }    
        return ans;
    }
};
