class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& a, int k) {
        int n=a.size();
        vector<int>dp(n+1,1);

        for(int i=n-2;i>=0;i--)
        {
            if(a[i]!=a[i+1])
            dp[i]=1+dp[i+1];
        }
        int ans=0;
        for(int i=0;i<n;i++)
        {

            if(i+k-1<n)
            {
                if(dp[i]>=k)
                ans++;
            }
            else
            {
                if(a[n-1]==a[0])
                continue;
                if(dp[i]<n-i)
                continue;
                if(dp[0]<(k-(n-i)))
                continue;
                ans++;
            }
        }
        return ans;
    }
};
