class Solution:
    def numberOfAlternatingGroups(self, a: List[int], k: int) -> int:
        n = len(a)
        b = [0] * (2 * n)
        ans = 0
        for i in range(2 * n) :
            if i < n :
                b[i] = a[i]
            else :
                b[i] = a[i - n]
        r = -1
        for i in range(n) :
            if i <= r :
                if r - i + 1 >= k :
                    ans += 1
                    continue
            r = i
            for j in range(i + 1, i + n) :
                if b[j] == b[j - 1] :
                    break
                else :
                    r = j
            if r - i + 1 >= k :
                ans += 1

        return ans
