class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int K) {
        int n = (int) colors.size();

        vector<int> pre(n + n + 1, 0);

        for (int i = 0; i < n + n; ++i) {
            if (i == 0 || colors[i % n] != colors[(i - 1) % n]) {
                pre[i + 1] = pre[i];
            } 
            pre[i + 1] += 1;
        }

        int res = 0;
        for (int i = 0; i < n; ++i) {
            if (pre[i + K] >= K) {
                ++res;
            }
        }

        return res;
    }
};
