class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        int n = colors.size(), res = 0;

        int i = 0, j = 0;

        while (i < n) {
            while (1) {
                int j2 = (j+1) % n;
                if (j2 != i && colors[j2] != colors[j]) j = j2;
                else break;
            }
            //[i, j]



            int L = j - i + 1;
            if (i > j) L = n + 1 - (i - j);

            if(L - k + 1 > 0) res++;


            // cout << i << " " << j << " " << L << " " << max(L - k + 1, 0) << " " << res << "\n";


            if (i == j) j = i+1;
            i++;
            // cout << i << " " << j << "?\n\n";

        }




        return res;
    }
};
