class Solution:
    def numberOfAlternatingGroups(self, arr: List[int], k: int) -> int:
        arr += arr[:k-1]
        n = len(arr)
        ans = i = 0
        j = 1
        while j < n:
            while j < n and j - i + 1 < k and arr[j] != arr[j - 1]:
                j += 1
