class Solution {
public:
    int numberOfAlternatingGroups(vector<int>& colors, int k) {
        vector<int> vec;

        for(int i : colors){
            vec.push_back(i);
        } 

        for(int i=0;i<k-1;i++){
            vec.push_back(colors[i]);
        }

        int res = 0;

        for(int i=0;i<vec.size();i++){
            int cnt = 1;
            for(int k=i+1;k<vec.size();k++) {
                if(vec[k] != vec[k - 1]) cnt++;
                else {
                    i = k - 1;
                    break;
                }
                if(k == vec.size() - 1) {
                    i = 3 * vec.size();
                    break;
                }
            }
            res += max(0, cnt - k + 1);
        }
        return res;
    }
};
